+import React, { useState, useEffect } from "react";
+import { useNavigate } from "react-router-dom";
+
+function Category({
+  api,
+  addToCart,
+  searchTerm,
+  highlightText,
+  addToWishList,
+  setSelectedProduct,
+  selectedCategory,
+}) {
+  const [items, setItems] = useState([]);
+
+  const [filteredItems, setFilteredItems] = useState([]);
+
+  // Fetch items from db.json (JSON Server)
+  useEffect(() => {
+    fetch(`${api}/products`)
+      .then((response) => response.json())
+      .then((data) => {
+        setItems(data); // Store all items
+        setFilteredItems(data); // Set initial filtered items
+      })
+      .catch((error) => console.error("Error fetching data:", error));
+  }, []);
+
+  // Filter items based on selected category
+  useEffect(() => {
+    if (selectedCategory === "All") {
+      setFilteredItems(items);
+    } else {
+      setFilteredItems(
+        items.filter((item) => item.category === selectedCategory)
+      );
+    }
+  }, [selectedCategory, items]); // Re-run filter when category or items change
+
+  const filteredProducts = filteredItems.filter(
+    (product) =>
+      product.category.toLowerCase().includes(searchTerm.toLowerCase()) ||
+      product.name.toLowerCase().includes(searchTerm.toLowerCase())
+  );
+
+  const navigate = useNavigate();
+  const handleProductClick = (product) => {
+    setSelectedProduct(product);
+    navigate("/selectedProduct");
+  };
+
+  return (
+    <>
+      <div style={{ marginTop: "20px", display: "flex", flexWrap: "wrap" }}>
+        {filteredProducts.length > 0 ? (
+          filteredProducts.map((product) => (
+            <div
+              key={product.id}
+              style={{
+                border: "1px solid #ddd",
+                borderRadius: "8px",
+                maxWidth: "400px",
+                maxHeight: "600px",
+                margin: "15px",
+              }}
+            >
+              {product.images.length > 0 ? (
+                <img
+                  src={product.images[0]} // Display first image only
+                  alt={product.name}
+                  style={{
+                    width: "300px",
+                    height: "300px",
+                    objectFit: "cover",
+                    borderRadius: "8px",
+                  }}
+                />
+              ) : (
+                <p>No image available</p>
+              )}
+              <span className="text">
+                <h5 className="name">
+                  <span
+                    style={{ color: "black" }}
+                    dangerouslySetInnerHTML={{
+                      __html: highlightText(product.name, searchTerm),
+                    }}
+                  ></span>{" "}
+                </h5>
+                <h5>Description: {product.desc}</h5>
+                <h5>Price: {product.price}</h5>
+                <div>
+                  <button onClick={() => addToCart(product)}>addToCart</button>{" "}
+                  <button >+wisList</button>
+                </div>
+              </span>
+            </div>
+          ))
+        ) : (
+          <p>Loading...</p>
+        )}
+      </div>
+    </>
+  );
+}
+
+export default Category;

